function  Item ()  {
    const [itemName, setItemName] = useState(null);
    const [abc, setAbc] = useState("")

    useEffect(() => {
        const fetchData= ()=>{
            axios.get('http://127.0.0.1:8000/items/').then((res) =>{
                console.log(res.data);
                setItemName(res.data.results);
            })
        }
        fetchData();
        setAbc('2')
    },[])
    

    // return (
    //     <div className='App'>
