import good from '../../assets/img/images/good.png';
import veryGood from '../../assets/img/images/very-good.png';
import excellent from '../../assets/img/images/excellent.png';
import notGood from '../../assets/img/images/not-good.png';
import x from '../../assets/img/images/x.png';
import { Link } from 'react-router-dom';
import axios from 'axios'
import { useContext } from 'react';
import { ReviewContext } from '../../ReviewContext';
import { useState } from 'react';

const WriteReview = () => {
    const {value, setValue} = useContext(ReviewContext)
    const [name, setName] = useState('')
    const [reviewText, setReviewText] = useState('')
    const [reviewLikes, setReviewLikes] = useState(null)

    const handleName = (event) => {
        
    }

    const postReview = () => {
        axios.post('http://127.0.0.1:8000/reviews/'), {
            name: name,
            review: reviewText,
            likes: reviewLikes,
        }
    }

    return (
        <div class="prompt-box">
            <h1> {value} </h1>
            <form>
                <Link to="/">
                    <img class="x" src={x} />
                </Link>
                <h1>Write Review</h1>
                <p>Choose your thought</p>
                <div class="prompt-buttons">
                    <button> <img src={good} /> </button>
                    <button> <img src={veryGood} /> </button>
                    <button> <img src={excellent} /> </button>
                    <button> <img src={notGood} /> </button>
                </div>
                <input type="text" id="name" name="name" placeholder="Enter your name"></input>
                <textarea id="review-text" name="review-text" placeholder="Enter your review"></textarea>
                <input type="submit" id="review-submit" value="Send Review"></input>
            </form>
        </div>
)}

export default WriteReview
