    return (
        <div className='App'>
            {item && item.length > 0 && item.map((i) => (
                <div className='item'>
                    <img class="reviews-image" src={i.image}></img>
                            <div className="item-details">
                                <h1>{i.name}</h1>
                                <div class="reviews">
                                    <div class="reviews-icon">
                                    <img src={Heart}></img>
                                    </div>
                                    <p class="reviews-number">(16)</p>
                                    <div class="check-write-reviews">
                                        <p>Check Reviews</p>
                                        <p>Write Review</p>
                                    </div>
                                </div>
                                <div class="price-add">
                                    <p class="price">$16</p>
                                    <button class="add">Add +</button>
