import React, { createContext, useReducer, useState } from 'react'

const AppContext = createContext()

const initialState = {
    itemReviews: null
}

function ContextProvider({children}) {
    const [state, setState] = useState(initialState)

    function updateItemReviews() {
        setState()
    }
}

export { AppContext }
