import { useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import X from '../../assets/img/images/x.png';
import axios from 'axios';
import { useState } from 'react';
import { ReviewContext } from '../../ReviewContext';

const Reviews = () => {
    const {value, setValue} = useContext(ReviewContext)

    const [reviews, setReviews] = useState(null)

    useEffect(() => {
        const fetchReviews = () => {
            axios.get('http://127.0.0.1:8000/reviews/').then((res) => {
                const filteredResults = res.data.results.filter(res => res.item === 2);
                setReviews(filteredResults);
                console.log(filteredResults);
            })
        }

        fetchReviews();
    }, [])

    return (
        <div class="prompt-box">
            <h1> {msg} </h1>
            <Link to='/'>
            <img class="x" src={X}></img>
            </Link>
            <h1>Reviews for "Chicken Makhani Burst + Lite"</h1>
            <div class="review">
                {reviews && reviews.length > 0 && reviews.map((review) => (
                    <h1>{review.name}</h1>
                ))}
            </div>
        </div>
    )
}

export default Reviews
