import ItemImg from '../../assets/img/images/item.png'
import Heart from '../../assets/img/images/heart.png';
import axios from 'axios';
import { useState } from 'react';
import { useEffect } from 'react';

const Item = () => {
    const [itemName, setItemName] = useState(null);

    useEffect(() => {
        axios.get('http://127.0.0.1:8000/items/').then((response) => {
            setItemName(response.data)
        })
    })

    return (
        <section class="items">
                <ul>
                    {itemName.map(item => (
                        <li key={item.id} >{item.name}</li>
                    ))}
                    <li>
                        <div class="item">
                            <img class="reviews-image" src={ItemImg}></img>
                            <div class="item-details">
                                <h1>{itemName}</h1>
                                <div class="reviews">
                                    <div class="reviews-icon">
                                    <img src={Heart}></img>
                                    </div>
                                    <p class="reviews-number">(16)</p>
                                    <div class="check-write-reviews">
                                        <p>Check Reviews</p>
                                        <p>Write Review</p>
                                    </div>
                                </div>
                                <div class="price-add">
                                    <p class="price">$16</p>
                                    <button class="add">Add +</button>
                                </div>
                            </div>
                        </div>
                    </li>
                    
                </ul>
            </section>
    )
}

export default Item
